import React, { useState, useEffect, useRef } from "react";
import { Typography, Button, TextField } from "@mui/material";
import { useParams, Link } from "react-router-dom";
import { fetchModel } from "../../lib/fetchModelData";
import axios from "axios";
import "./styles.css";

const fetchUserById = async (userId) => {
  try {
    const userDetails = await axios.get(
      `http://localhost:8081/api/user/${userId}`
    );
    return userDetails.data
      ? `${userDetails.data.first_name} ${userDetails.data.last_name}`
      : "Unknown User";
  } catch (error) {
    console.error("Error fetching user data:", error);
    return "Unknown User";
  }
};
const fetchUsersForComments = async (photos) => {
  try {
    // Map over all comments and fetch user details asynchronously
    const userPromises = photos.flatMap((photo) =>
      photo.comments.map((comment) => fetchUserById(comment.user_id))
    );
    const users = await Promise.all(userPromises);
    return users;
  } catch (error) {
    console.error("Error fetching user data:", error);
    return [];
  }
};
function UserPhotos({ userLoginId }) {
  const { userId } = useParams();
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [commentUsers, setCommentUsers] = useState([]);
  const [commentTexts, setCommentTexts] = useState([]);

  const [comment, setComment] = useState("");

  const fetchUserPhotos = async () => {
    try {
      const userPhotos = await axios.get(`http://localhost:8081/api/photo/photosOfUser/${userId}`);
      setPhotos(userPhotos.data);

      // Fetch user details
      const userDetails = await axios.get(`http:localhost:8081/api/user/${userId}`);
      setUser(userDetails.data);
    } catch (error) {
      console.error("Error fetching user photos:", error);
    }
  };
  useEffect(() => {
    fetchUserPhotos();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  useEffect(() => {
    // Fetch user details for all comments when photos change
    const fetchUsers = async () => {
      const users = await fetchUsersForComments(photos);
      setCommentUsers(users);
    };

    fetchUsers();
  }, [photos]);

  const handleCommentSubmit = async (photoId) => {
    try {
      const formData = {
        comment : comment,
        date_time: new Date(),
        photo_id : photoId,
        userId: userLoginId,
      }
      await axios.post("http://localhost:8081/api/photo/commentsOfPhoto/add", formData);

      setComment("");

      // Refetch the photos to update the comments
      fetchUserPhotos();
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  return (
    <div>
      <Typography variant="h4" className="header">
        User Photos
      </Typography>
      {userLoginId === userId && (
        <Button
          variant="contained"
          color="primary"
          component={Link}
          to="/photos/add"
          className="add-photo-button"
        >
          Add Photo
        </Button>
      )}
      <div className="user-photos-container">
        {photos.map((photo) => (
          <div key={photo._id} className="photo-container">
            <img
              src={`http://localhost:8081/api/photo/images/${photo.file_name}`}
              alt={user?.first_name}
              className="photo"
            />
            <Typography className="posted-time">
              Posted time: {(photo.date_time)}
            </Typography>
            {/* UI for adding comments */}
            <TextField
              label="Add a comment"
              variant="outlined"
              value={comment}// Lấy giá trị từ commentTexts
              onChange={handleCommentSubmit} // Cập nhật giá trị trong commentTexts
            />
            <Button
              variant="contained"
              color="primary"
              onClick={() => handleCommentSubmit(photo._id)}
            >
              Add Comment
            </Button>
            {/* Display existing comments */}
            <Typography className="comments-header">Comments:</Typography>
            {photo.comments &&
              photo.comments.map((comment, commentIndex) => (
                <div
                  key={comment._id}
                  className={`comment ${
                    comment.user_id === userId
                      ? "user-comment"
                      : "other-comment"
                  }`}
                >

                  <Typography className="comment-time">
                  {comment.comment} ({(comment.date_time)})
                  </Typography>
                  <Typography className="comment-text">

                  </Typography>
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default UserPhotos;
