import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import { Typography, Divider, TextField, Button } from "@material-ui/core";

function Register() {
  const [loginName, setLoginName] = useState("");
  const [passWord, setPassWord] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [location, setLocation] = useState("");
  const [description, setDescription] = useState("");
  const [occupation, setOccupation] = useState("");
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [registrationError, setRegistrationError] = useState("");
  
  const handleRegistration = async () => {
    if(passWord !== confirmPassword) {
      setRegistrationError("Mật khẩu không trùng khớp")
    }

    const formData = {
      loginName,
      passWord,
      firstName,
      lastName,
      location,
      description,
      occupation,
    };

    const response = await axios.post(
      "http://localhost:8081/api/credential/register",
      formData
    );

    if (response.data.success) {
      setRegistrationSuccess(true);
    } else {
      setRegistrationError("Tên đăng nhập đã tồn tại. Vui lòng chọn tên khác.");
    }
  };
  return (
    <div className="registercontainer">
      <div className="register-form">
      <Typography variant="h2">Đăng ký</Typography>
      {!registrationSuccess ? (
        <form onSubmit={handleRegistration}>
          <TextField
            type="text"
            label="Login Name"
            value={loginName}
            onChange={(e) => setLoginName(e.target.value)}
          />
          <TextField
            type="password"
            label="Password"
            value={passWord}
            onChange={(e) => setPassWord(e.target.value)}
          />
          <TextField
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
          <TextField
            type="text"
            label="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
          <TextField
            type="text"
            label="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
          <TextField
            type="text"
            label="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <TextField
            type="text"
            label="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <TextField
            type="text"
            label="Occupation"
            value={occupation}
            onChange={(e) => setOccupation(e.target.value)}
          />
          <Button type="submit" variant="contained" color="primary">Đăng ký</Button>
          {registrationError && (
            <Typography className="error-message">{registrationError}</Typography>
          )}
        </form>
      ) : (
        <Typography>Đăng ký thành công!</Typography>
      )}
      <div className="back-link">
          <Link to="/login">Login</Link>
        </div>
        </div>
    </div>
  );
}

export default Register;
