import React, { useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Typography, Divider, TextField, Button } from "@material-ui/core";

function LoginForm({ onLogin }) {
  const [loginName, setLoginName] = useState("");
  const [passWord, setPassWord] = useState("");
  const [loginError, setLoginError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8081/api/credential/login", {
        loginName,
        passWord,
      });

      if (response.data.success) {
        setLoginError("");
        const userData = response.data.user;
        localStorage.setItem("user", JSON.stringify(userData)); // Lưu thông tin người dùng vào Local Storage
        onLogin(userData);
        navigate(`/users/${userData._id}`);
      } else {
        setLoginError(response.data.message);
      }
    } catch (error) {
      console.log(error);
    }
  };

  
  return (
    <div className="login-container">
      <div className="login-form">
      <Typography variant="h2">Login</Typography>
      <form onSubmit={handleLogin}>
        <TextField
          type="text"
          label="Username"
          value={loginName}
          onChange={(e) => setLoginName(e.target.value)}
        />
        <TextField
          type="password"
          label="Password"
          value={passWord}
          onChange={(e) => setPassWord(e.target.value)}
        />

        <Button type="submit" variant="contained" color="primary">Đăng nhập</Button>

        {loginError && <Typography className="error-message">{loginError}</Typography>}
      </form>
      </div>
    </div>
  );
}

export default LoginForm;
