import React, { useState, useEffect, useRef } from "react";
import { Typography, Button, TextField } from "@mui/material";
import { useParams, Link } from "react-router-dom";
import fetchModel from "../../../lib/fetchModelData";
import axios from "axios";
import "./styles.css";

function UserPhotos({ userLoginId }) {
  const { userId } = useParams();

  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [commentUsers, setCommentUsers] = useState([]);
  const [commentTexts, setCommentTexts] = useState([]);

  const fetchUserById = async (userId) => {
    try {
      const userDetails = await fetchModel(`/api/user/${userId}`);
      return userDetails
        ? `${userDetails.first_name} ${userDetails.last_name}`
        : "Unknown User";
    } catch (error) {
      console.error("Error fetching user data:", error);
      return "Unknown User";
    }
  };
  const fetchUsersForComments = async (photos) => {
    try {
      // Map over all comments and fetch user details asynchronously
      const userPromises = photos.flatMap((photo) =>
        photo.comments.map((comment) => fetchUserById(comment.user_id))
      );
      const users = await Promise.all(userPromises);
      return users;
    } catch (error) {
      console.error("Error fetching user data:", error);
      return [];
    }
  };
  const fetchUserPhotos = async () => {
    try {
      const data = await axios.get(`/api/photo/photosOfUser/${userId}`);
      setPhotos(data);
    } catch (error) {
      console.error("Error fetching user photos:", error);
    }
  };
  useEffect(() => {
    fetchUserPhotos();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  useEffect(() => {
    // Fetch user details for all comments when photos change
    const fetchUsers = async () => {
      const users = await fetchUsersForComments(photos);
      setCommentUsers(users);
    };

    fetchUsers();
  }, [photos]);

  const handleCommentSubmit = async (photoId) => {
    try {
      const response = await fetch(
        `https://22qwnp-8081.csb.app/api/photo/commentsOfPhoto/${photoId}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            comment: commentTexts[photoId],
            userId: userLoginId,
          }), // Sử dụng commentTexts[photoId] thay vì commentText
        }
      );
      if (!response.ok) {
        throw new Error("Failed to submit comment");
      }

      // Clear the comment input field
      setCommentTexts((prevState) => ({ ...prevState, [photoId]: "" }));

      // Refetch the photos to update the comments
      fetchUserPhotos();
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  return (
    <div className="user-photos-container">
      <Typography variant="body1">User Photos for User ID: {userId}</Typography>
      {userLoginId === userId && (
        <div>
          <Button
            variant="contained"
            color="primary"
            component={Link}
            to="/photos/add"
          >
            Add Photo
          </Button>
        </div>
      )}
      {photos.length === 0 ? (
        <div>No photos found for this user.</div>
      ) : (
        photos.map((photo) => (
          <div key={photo._id} className="photo-container">
            <img
              src={"/images/"+ photo.file_name}
              alt={`Photo by user ${userId}`}
            />
            <div className="photo-details">
              <Typography variant="body2">Time: {photo.date_time}</Typography>
              <Typography variant="h6">Comments</Typography>
              <div className="comments-section">
                {photo.comments.map((comment) => (
                  <div key={comment._id} className="comment-item">
                    <Typography variant="body2">
                      {comment.user_name}: {comment.comment}
                    </Typography>
                    <Typography variant="body2">
                      Time: {comment.date_time}
                    </Typography>
                  </div>
                ))}
              </div>
              <TextField
              label="Add a comment"
              variant="outlined"
              value={commentTexts[photo._id] || ""} // Lấy giá trị từ commentTexts
              onChange={(e) =>
                setCommentTexts((prevState) => ({
                  ...prevState,
                  [photo._id]: e.target.value,
                }))
              } // Cập nhật giá trị trong commentTexts
            />
              <Button
              variant="contained"
              color="primary"
              onClick={() => handleCommentSubmit(photo._id)}
            >
                Add Comment
              </Button>
            </div>
            <Typography className="comments-header">Comments:</Typography>
            {photo.comments &&
              photo.comments.map((comment, commentIndex) => (
                <div
                  key={comment._id}
                  className={`comment ${
                    comment.user_id === userId
                      ? "user-comment"
                      : "other-comment"
                  }`}
                >
                  <Typography className="user">
                    {commentUsers[commentIndex]}{" "}
                  </Typography>
                  <Typography className="comment-time">
                    {formatDateTime(comment.date_time)}
                  </Typography>
                  <Typography className="comment-text">
                    {comment.comment}
                  </Typography>
                </div>
              ))}
          </div>
        ))
      )}
    </div>
  );
}

export default UserPhotos;
